import React, { useState, useEffect } from 'react';
import { createClient } from '@supabase/supabase-js';
import './App.css';

// Initialize Supabase client
const supabase = createClient(
  'https://ilhpnfuxslmnmafyfkjm.supabase.co',
  'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpc3MiOiJzdXBhYmFzZSIsInJlZiI6ImlsaHBuZnV4c2xtbm1hZnlma2ptIiwicm9sZSI6ImFub24iLCJpYXQiOjE3MzI4NDAxMzYsImV4cCI6MjA0ODQxNjEzNn0.XkHj-525jePRxoOAsbrAlMhmfc1qaYMtD0iP2PelKJI'
);

function App() {
  const [session, setSession] = useState(null);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    supabase.auth.getSession().then(({ data: { session } }) => {
      setSession(session);
    });

    const {
      data: { subscription },
    } = supabase.auth.onAuthStateChange((_event, session) => {
      setSession(session);
    });

    return () => subscription.unsubscribe();
  }, []);

  const handleLogin = async () => {
    try {
      setLoading(true);

      await supabase.auth.signInWithOAuth({
        provider: 'azure',
        options: {
          skipBrowserRedirect: false,
          redirectTo: 'http://localhost:3000',
        }
      });

    } catch (error) {
      console.error('Error:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleLogout = async () => {
    await supabase.auth.signOut();
  };

  return (
    <div className="App">
      {!session ? (
        <button 
          onClick={handleLogin}
          disabled={loading}
          className="login-button"
        >
          {loading ? 'Loading...' : 'Sign in with Microsoft'}
        </button>
      ) : (
        <div className="logged-in">
          <p>Logged in as: {session.user.email}</p>
          <button onClick={handleLogout} className="logout-button">
            Sign Out
          </button>
        </div>
      )}
    </div>
  );
}

export default App;
