import React, { useState } from 'react';
import { PublicClientApplication, InteractionRequiredAuthError } from '@azure/msal-browser';
import './App.css';

// MSAL configuration
const msalConfig = {
  auth: {
    clientId: 'your-azure-client-id', // Replace with your Azure AD client ID
    authority: 'https://login.microsoftonline.com/common',
    redirectUri: 'http://localhost:3000',
    postLogoutRedirectUri: 'http://localhost:3000',
  },
  cache: {
    cacheLocation: 'sessionStorage',
    storeAuthStateInCookie: false,
  }
};

// Create MSAL instance
const msalInstance = new PublicClientApplication(msalConfig);

function App() {
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [user, setUser] = useState(null);

  const loginRequest = {
    scopes: ['openid', 'profile', 'email', 'User.Read']
  };

  const handleLogin = async () => {
    try {
      setLoading(true);
      setError(null);

      const response = await msalInstance.loginPopup(loginRequest);

      if (response) {
        setUser(response.account);

        // Get access token
        const tokenResponse = await msalInstance.acquireTokenSilent({
          ...loginRequest,
          account: response.account
        });

        // Here you can send the token to your backend or Supabase
        console.log('Access Token:', tokenResponse.accessToken);
      }
    } catch (err) {
      if (err instanceof InteractionRequiredAuthError) {
        return msalInstance.acquireTokenPopup(loginRequest);
      }
      setError(err.message);
      console.error('Login error:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleLogout = async () => {
    try {
      await msalInstance.logoutPopup();
      setUser(null);
    } catch (err) {
      console.error('Logout error:', err);
    }
  };

  return (
    <div className="App">
      <div className="auth-container">
        {!user ? (
          <button 
            onClick={handleLogin} 
            className="login-button"
            disabled={loading}
          >
            {loading ? 'Loading...' : 'Sign in with Microsoft'}
          </button>
        ) : (
          <div className="user-info">
            <p>Welcome, {user.name}</p>
            <p>Email: {user.username}</p>
            <button 
              onClick={handleLogout}
              className="logout-button"
            >
              Sign Out
            </button>
          </div>
        )}

        {error && (
          <div className="error-message">
            {error}
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
