import React, { useState } from 'react';
import { createClient } from '@supabase/supabase-js';
import './App.css';

const supabaseUrl = 'https://ilhpnfuxslmnmafyfkjm.supabase.co';
const supabaseAnonKey = 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpc3MiOiJzdXBhYmFzZSIsInJlZiI6ImlsaHBuZnV4c2xtbm1hZnlma2ptIiwicm9sZSI6ImFub24iLCJpYXQiOjE3MzI4NDAxMzYsImV4cCI6MjA0ODQxNjEzNn0.XkHj-525jePRxoOAsbrAlMhmfc1qaYMtD0iP2PelKJI'; // Replace with your actual anon key

// Initialize Supabase with PKCE flow
const supabase = createClient(supabaseUrl, supabaseAnonKey, {
  auth: {
    flowType: 'pkce',
    autoRefreshToken: true,
    persistSession: true,
    detectSessionInUrl: true
  }
});

function App() {
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);

  const signInWithMicrosoft = async () => {
    try {
      setLoading(true);
      setError(null);

      const { data, error: authError } = await supabase.auth.signInWithOAuth({
        provider: 'azure',
        options: {
          skipBrowserRedirect: false,
          redirectTo: 'http://localhost:3000',
          scopes: 'email openid profile offline_access',
          queryParams: {
            response_type: 'code',
            code_challenge_method: 'S256'
          }
        }
      });

      if (authError) throw authError;
      console.log('Auth response:', data);

    } catch (err) {
      setError(err.message);
      console.error('Auth error:', err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="App">
      <div className="auth-container">
        <button 
          onClick={signInWithMicrosoft} 
          className="login-button"
          disabled={loading}
        >
          {loading ? 'Loading...' : 'Sign in with Microsoft'}
        </button>

        {error && (
          <div className="error-display">
            <p className="error-message">{error}</p>
            <button 
              onClick={() => setError(null)}
              className="clear-error-button"
            >
              Clear Error
            </button>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
